mapper.update:
  	UPDATE AUCTIONS
   SET title=#{title}, content=#{content}, img=#{img}, startPrice=#{startPrice}, 
		UPLOADDATE=#{uploadDate}, ENDDATE=#{endDate}, COUNT=#{count}, MAXPRICE=#{maxPrice}, STATE=#{state}
 	WHERE  AUCTIONID=#{auctionId}

