mapper.update:
 	UPDATE GROUPBUYS
  SET TITLE=#{title}, CONTENT=#{content}, IMG=#{img}, MINNO=#{minNo}, UPLOADDATE=to_char(sysdate,'yyyy-mm-dd'), 
  	ENDDATE=#{endDate}, COUNT=#{count}, STATE=#{state}, RATE=#{rate}, PARTICIPANTS=#{participants}, CATID=#{catId}, PRICE=#{price}
	WHERE  GROUPBUYID=#{groupBuyId}

mapper.insert:

  	INSERT INTO OPTIONS (OPTIONID, GROUPBUYID, CONTENT)
  	SELECT OPTIONID_SEQ.nextval, A.* FROM (

			SELECT  #{groupBuyId}, #{option}
			FROM 	DUAL

		) A
